/**
 * @namespace batch
 * @memberof Alarms
 */
function batch () {
  /**
   * Start an alarm batch
   *
   * @memberof Alarms.batch
   * @param  {string} action Action you want to perform
   * @param  {Object} query Query to select which alarms should be targeted
   * @return {Promise<Batch>}
   */
  const raw = (...args) => this._batch('alarm', ...args)

  /**
   * Remove alarms in batch
   *
   * @memberof Alarms.batch
   * @param  {string[]} ids Array of alarm id's you want to remove
   * @return {Promise<Batch>}
   */
  const remove = ids => raw('delete', { ids })

  /**
   * Update alarms in batch
   *
   * @memberof Alarms.batch
   * @param  {string[]} ids Array of alarm id's you want to update
   * @param  {object} params Properties that should be update
   * @return {Promise<Batch>}
   */
  const update = (ids, params) => raw('update', { ids }, params)

  return {
    raw,
    remove,
    update
  }
}

module.exports = batch
