const Resource = require('../resource')

/**
 * @namespace Permissions
 */
class Permissions extends Resource {
  /**
   * Get all permissions
   * @return {Promise<Permission[]>}
   */
  list () {
    return this._request({
      path: '/permissions'
    })
  }

  /**
   * Get a single permission by id
   * @param  {string} id
   * @return {Promise<Permission>}
   */
  get (id) {
    return this._request({
      path: `/permissions/${id}`
    })
  }

  /**
   * Get all permission concepts
   * @return {Promise<PermissionConcept[]>}
   */
  concepts () {
    return this._request({
      path: '/permissions/concepts'
    })
  }
}

module.exports = Permissions
