const Resource = require('../resource')

/**
 * @namespace PredefinedRoles
 */
class PredefinedRoles extends Resource {
  /**
   * Get all predefined roles
   * @return {Promise<PredefinedRole[]>}
   */
  list () {
    return this._request({
      path: '/roles/predefined'
    })
  }
}

module.exports = PredefinedRoles
