const Resource = require('../resource')

/**
 * @namespace Tenants
 */
class Tenants extends Resource {
  /**
   * Get the branding settings of a tenant
   * @param {Object} query optional options for fetching branding settings.
   * @param {string} query.hostname optional hostname linked to a tenant
   */
  branding (query = {}) {
    const { hostname } = query

    return this._request({
      path: '/tenants/branding',
      params: {
        ...hostname && { hostname }
      }
    })
  }
}

module.exports = Tenants
