/**
 * @namespace batch
 * @memberof Resources
 */
function batch () {
  /**
   * Start a resource batch
   *
   * @memberof Resources.batch
   * @param  {string} action Action you want to perform
   * @param  {Object} query Query to select which resources should be targeted
   * @return {Promise<Batch>}
   */
  const raw = (...args) => this._batch('resource', ...args)

  /**
   * Remove resources in batch
   *
   * @memberof Resources.batch
   * @param  {string[]} ids Array of resource id's you want to remove
   * @return {Promise<Batch>}
   */
  const remove = ids => raw('delete', { ids })

  return {
    raw,
    remove
  }
}

module.exports = batch
