'use strict'

const Resource = require('./resource')

/**
*  @namespace Stats
 * @description Allows you to retrieve Waylay application statistics
 */
class Stats extends Resource {
  /**
   * Retrieve metrics
   *
   * @param  {object} optional params containing list of metrics, from and until
   * @return {Promise}
   */
  getMetrics (params) {
    return this._request({ path: '/metrics', params })
  }

  /**
   * Retrieve metrics
   * @param  {string} name
   * @param  {object} optional params containing from and until
   * @return {Promise}
   */
  getMetric (name, params) {
    return this._request({ path: `/metrics/${name}`, params })
  }
}

module.exports = Stats
