/**
 * @namespace batch
 * @memberof Types
 */
function batch () {
  /**
   * Start a resourcetype batch
   *
   * @memberof Types.batch
   * @param  {string} action Action you want to perform
   * @param  {Object} query Query to select which resourcetypes should be targeted
   * @return {Promise<Batch>}
   */
  const raw = (...args) => this._batch('resourcetype', ...args)

  /**
   * Remove resourcetypes in batch
   *
   * @memberof Types.batch
   * @param  {string[]} ids Array of resourcetype id's you want to remove
   * @return {Promise<Batch>}
   */
  const remove = ids => raw('delete', { ids })

  return {
    raw,
    remove
  }
}

module.exports = batch
